E_D("replace into `phome_ecms_infotmp_news` values('476','326','http://www.61baobao.com/jiaoan/201108/11491.html','0','2012-06-17 02:55:55','幼儿园墙面：蜗牛聚会―幼儿园图片','2012-06-17 02:55:55','4usky','2','1339872955','','','','            <p>本文Tag标签：</p>            <p>导读：</p>          ','','',' <IMG height=346 alt=幼儿园墙面布置图片欣赏 src=\"/d/file/2011-08/155609f15b309a3b00c4d40f468367e0.gif\" width=504 align=center border=1> </div>          ','0','');");
