E_D("replace into `phome_ecms_infotmp_news` values('1849','327','http://www.61baobao.com/jiaoan/201108/11545.html','0','2012-06-17 03:07:29','雪山和松树―幼儿园图片','2012-06-17 03:07:29','4usky','2','1339873649','','','','            <p>本文Tag标签：</p>            <p>导读：		              		                	　    </p>          ','','',' 		  <DIV> <DIV><IMG height=484 src=\"/d/file/2011-08/4692805eef761247791e19c1974139bf.gif\" width=642 align=left alt=\"幼儿园主题墙面(饰)布置图片\" border=1></DIV></DIV>           		                	　     </div>          ','0','');");
