E_D("DROP TABLE IF EXISTS `phome_enewszt`;");
E_C("CREATE TABLE `phome_enewszt` (
  `ztid` smallint(6) NOT NULL AUTO_INCREMENT,
  `ztname` varchar(60) NOT NULL DEFAULT '',
  `onclick` int(11) NOT NULL DEFAULT '0',
  `ztnum` tinyint(4) NOT NULL DEFAULT '0',
  `listtempid` smallint(6) NOT NULL DEFAULT '0',
  `ztpath` varchar(255) NOT NULL DEFAULT '',
  `zttype` varchar(10) NOT NULL DEFAULT '',
  `newline` int(11) NOT NULL DEFAULT '0',
  `zturl` varchar(200) NOT NULL DEFAULT '',
  `hotline` tinyint(4) NOT NULL DEFAULT '0',
  `goodline` tinyint(4) NOT NULL DEFAULT '0',
  `classid` smallint(6) NOT NULL DEFAULT '0',
  `hotplline` tinyint(4) NOT NULL DEFAULT '0',
  `firstline` tinyint(4) NOT NULL DEFAULT '0',
  `islist` tinyint(1) NOT NULL DEFAULT '0',
  `maxnum` int(11) NOT NULL DEFAULT '0',
  `tid` smallint(6) NOT NULL DEFAULT '0',
  `tbname` varchar(60) NOT NULL DEFAULT '',
  `reorder` varchar(50) NOT NULL DEFAULT '',
  `intro` text NOT NULL,
  `ztimg` varchar(255) NOT NULL DEFAULT '',
  `jstempid` smallint(6) NOT NULL DEFAULT '0',
  `zcid` smallint(6) NOT NULL DEFAULT '0',
  `showzt` tinyint(1) NOT NULL DEFAULT '0',
  `ztpagekey` varchar(255) NOT NULL DEFAULT '',
  `classtempid` smallint(6) NOT NULL DEFAULT '0',
  `myorder` smallint(6) NOT NULL DEFAULT '0',
  `nrejs` tinyint(1) NOT NULL DEFAULT '0',
  `usezt` tinyint(1) NOT NULL DEFAULT '0',
  `yhid` smallint(6) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ztid`),
  KEY `classid` (`classid`),
  KEY `zcid` (`zcid`),
  KEY `usezt` (`usezt`)
) ENGINE=MyISAM AUTO_INCREMENT=66 DEFAULT CHARSET=utf8");
E_D("replace into `phome_enewszt` values('1','宝宝最喜欢的儿歌','0','70','20','s/baobaozuixihuandeerge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','随着网络的发展，儿童歌曲也越做越好了，质量也越来越高，但宝宝们最喜欢听的儿歌还是我们都耳熟能详的那些儿歌，《数鸭子》《小毛驴》《小燕子》《小白兔乖乖》等等。这些都是时代的经典，不管哪个年代都是小孩子喜欢听的儿歌，我们听着一遍又一遍，总能令自己怀念起自己儿时的快乐与欢笑。','/d/file/p/6be88882671c083b81a42952b340f233.jpg','1','1','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('6','50年代儿歌童谣','0','70','20','s/50erge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      50年代新中国成立初期，是儿歌童谣创作的黄金时期。这一时期，涌现出大量的反映着这个时代儿童生活的儿歌童谣，有歌颂毛主席，歌颂共产党，歌颂解放军，歌颂土地改革，歌颂中苏联友谊，也有反映着那个时代的儿童日常生活的。内容丰富，精彩纷呈，其中《让我们荡起双桨》《听妈妈讲那过去的事情》等打动了一代又一代儿童的心……\r\n','/d/file/p/12d60d561b1e5cfbb763cdc0f0ddc245.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('7','60年代儿歌童谣','0','70','20','s/60erge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','上世纪的60年代，儿童歌谣延续着上世纪50年代的风格，比较注重儿童的思想道德建设，同时对新时期儿童的志向也有所记载。一些新的作品成为经典，其中的代表作当属后来被确定为中国少年先锋队队歌――《我们是共产主义接班人》，至今还在校园里传唱。此时，雷锋事迹广为传颂，儿童歌曲有《学习雷锋好榜样》、《一分钱》等，其中不少曲目成为幼儿歌曲中的经典。','/d/file/p/dff2de5c2a2be526bd2406b5c8a5882b.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('8','70年代儿歌童谣','0','70','20','s/70erge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','上个世纪70年代，儿童歌谣分为两种类，一类是批林批孔，另一类是歌颂“上山下乡”知青扎根农村。然而，真正在儿童中传唱主要是革命歌曲《我爱北京天安门》《红星照我去战斗》《闪闪的红星》等。1976年打倒“四人帮”，儿童歌谣迎来了第二个春天，反映儿童日常生活歌曲也不少，如《七色光之歌》《种太阳》《采蘑菇的小姑娘》等。','/d/file/p/70niandaige.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('9','80年代90年代儿歌童谣','0','70','20','s/80erge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','80年代儿歌出现了一个繁荣期。这一时期的儿歌含有很大的教育性，童稚、童真、童趣表现得非常到位。如刘饶民的《比一比》，教孩子们数数的《数字歌》、谜语歌等，这些都极大地丰富了儿童的生活。随着电视进入普通家庭，影视作品如外来动画片主题曲、台湾校园歌曲等成为孩子追捧的儿歌。这一时期儿歌的特点是旋律更多变化，感情更富有色彩。\r\n　　令人遗憾的是，上世纪90年代，我国鲜有优秀的儿童歌曲出现。孩子们更倾向于选择流行音乐，一些成人歌曲成为他们的“主旋律”。','/d/file/p/8090niandaige.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('10','2000年以后儿歌童谣','0','70','20','s/2000erge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','2000年以后，随着网络的普及和音频技术的发展，儿歌童谣的创作也加入大量的流行元素。承接上世纪90年代，儿童歌曲仍以动画片主题曲为主打；一些流传在校园儿童自编的新童谣，也反映了新时代孩子们的需求。这一时期，儿童歌谣“新”字更突出，风格更多元，时代性也更强。','/d/file/p/2000yihou.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('11','儿童小游戏','0','70','20','s/xiaoyouxi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','现在的宝宝，1岁就能自己操作电脑了，常听论坛里的妈妈们说，宝宝会自己打开桌面上的亲宝儿歌播放器听儿歌。爱玩是宝宝的天性，1-2岁的宝宝，已经会自己玩小游戏了哦，哪些小游戏是最适合宝宝玩并能增长宝宝智力的呢？我们精心挑选了准备给妈妈们。','/d/file/p/ertongxiaoyouxi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('12','圣诞儿歌专题','0','70','20','s/christmas','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      圣诞节作为外国的传统节日，但现如今我们也都记住了这个节日，因为圣诞老人的存在，使得小孩子每年都盼着这一天的到来，让所有的小朋友都充满着期待，等待着慈祥的圣诞老人发礼物。因为这个特殊的节日，也使得产生了各种好听的圣诞儿歌，妈妈们快教宝宝学唱几首圣诞儿歌吧。','/d/file/p/sengdanerge.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('13','剑桥少儿英语','0','70','20','s/jianqiao','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      剑桥少儿英语收集了最全的剑桥少儿英语系列资源分享给小朋友们，也是一个能让宝宝很快接触学习英语的好机会哦，是小朋友们学习英语的好帮手。','/d/file/p/jianqiaoshaoer.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('14','连连看_连连看小游戏','0','70','20','s/lianliankan','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      连连看作为益智游戏的一种，得道了广大群众的支持，运用于儿童小游戏当中也深受好评，得道广大年轻爸妈的支持，能够快速开发宝宝智力和行动能力的一个游戏，里面有从简单到复杂的各种连连看小游戏，等着你的宝宝来玩哦。','/d/file/p/lianliankanzhuanti.jpg','1','1','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('16','三只小猪学英语','0','70','20','s/xueyingyu','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      三只小猪学英语，通过形象、生动、有趣的表情动作和场景来演唱出一首首英文儿歌，让宝宝能够更加快速的记住这些英文字母和单词一级简单的语句。','/d/file/p/f2e667861ecc784e208f7233ca9a345f.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('17','幼儿歌曲_幼儿歌曲大全','0','70','20','s/youergequ','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      幼儿歌曲大全中罗列了一些列的好听的幼儿歌曲，你喜欢听的各种儿歌都能够满足，让你快速找到，都是广大宝宝喜欢的幼儿歌曲哦。','/d/file/p/youergequ.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('18','幼儿故事_幼儿故事大全','0','70','20','s/youergushi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      宝宝睡觉前都喜欢故事才能入睡，《小马过河》《小猫钓鱼》《丑小鸭》等都是宝宝爱听的，从古到今经典的故事都在其中，听了之后能让宝宝安详入睡。','/d/file/p/youergushi.jpg','1','1','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('19','亲宝儿歌经典系列','0','70','20','s/qinbao','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      亲宝经典系列儿歌，都是宝宝最爱听的儿歌，通过不断升级的用户体验，满足了广大用户，得到群众的支持，全部都是最经典。最好听的，内容丰富，精彩纷呈。《让我们荡起双桨》《数字歌》《种太阳》都是宝宝倒背如流的经典歌曲。','/d/file/p/jingdianxilie.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('20','彭野新儿歌','0','70','20','s/pengye','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      彭野，被誉为中国新儿歌之父，他的歌绝大部分人都听过，它代表着一个时代，所以我们要记住它。“彭野新儿歌，儿童流行歌”有着这样的美称，代表作有《爱我你就抱抱我》《爸爸妈妈听我说》相信不管大人小孩都听过。','/d/file/p/pengyexinerge.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('21','韩语儿歌','0','70','20','s/hanyu','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      好听的歌曲总是不分语言国家的，韩语儿歌当中同样有着许许多的经典之作，值得我们去学习、去欣赏。想想，你的孩子在生活中突然说几句韩语出来作为爸爸妈妈的你们是不是很高兴呢。','/d/file/p/hanyuerge.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('22','粤语儿歌视频大全','0','70','20','s/yueyu','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      好听的儿歌总是许许多多的，有我们都会的汉语，也有少数民族的，同样地方语言唱出来的儿歌也是很好听的。粤语唱的《何家小鸡何家猜》你是不是也听过呢，越来越多的粤语歌曲我们都耳熟能详了。','/d/file/p/yueyueerge.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('23','儿童智力测试小游戏','0','70','20','s/zhiliceshi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','宝宝的成长过程中，父母总会担心宝宝智力发育会遇到问题，比如不如邻居家小孩聪明啦，甚至担心智力低下啦。儿歌网收集整理了一些能帮助开发宝宝智力的儿童小游戏，旨在帮助父母更好的开发宝宝智力。另外强烈推荐同是亲宝旗下的专为开发宝宝智力而开发的网站：哆啦儿童乐园  http://www.duola.com','/d/file/p/ertongzhiliceshi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('24','春天的儿歌','0','70','20','s/chuntiandeerge','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      一年分四个季节：春夏秋冬，但喜欢春天的人是非常多的，因为那代表着复苏、开始与快乐，一年之计在于春，歌唱春天，歌唱美好的未来，《春天在哪里》《小燕子》就是其中的代表作品。\r\n','/d/file/p/chuntianerge.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('25','海绵宝宝小游戏','0','70','20','s/haimianbaobaoxiaoyouxi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      越来越多的卡通形象诞生了，但能让广大群众接受的却是不多。无疑，海绵宝宝就是其中的佼佼者，它用那搞怪的形象，趣味的表情，让人们记住了它。海绵宝宝制作成的小游戏同样受广大小朋友的喜欢，这里有各种各样的海绵宝宝小游戏等着小朋友的来玩哦，绝对让小朋友玩的高兴，它包括了搞笑小游戏、冒险小游戏、益智小游戏、动作小游戏等等等等，让小朋友玩的同时开发智力。','/d/file/p/haimianbaobao.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('26','我的妈妈-歌唱妈妈的儿歌','0','70','20','s/wodemama','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      妈妈，她代表了伟大、亲情和奉献。妈妈，为我们付出了太多太多，我们用一首首好听的歌曲献给她，献给我们最爱的妈妈。《妈妈的吻》《鲁冰花》都唱出了妈妈对孩子的爱、妈妈的好，一首《世上只有妈妈好》表达出了孩子们对妈妈的敬意。','/d/file/p/haomama.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('27','春天的成语','0','70','20','s/chuntiandechengyu','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      成语作为流传古今的词语，有着它独特的意义，它用简短的几个字表达出的含义却是相当丰富的，读起来朗朗上口，又方便记忆，简单明了。春天，万物复苏的时刻，我们一定得用成语这简明的字眼来表达它的壮丽。《寸草春晖》《阳春白雪》都是描述春天的成语。','/d/file/p/chuntiandechengyu.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('28','填色游戏-各种填色游戏大全','0','70','20','s/tianseyouxi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      适合儿童玩的游戏非常之多，但能锻炼小朋友的眼里、脑力、智力还有行动能力的话，却只有填色游戏了，游戏不仅要找好玩的，而且还要找有帮助的，填色游戏不得不说是个非常有必要的选择之一。各种各样的拼图游戏、积木游戏、人物卡通填色让宝宝玩的不亦乐乎。','/d/file/p/tianseyouxi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('29','小破孩系列','0','70','20','s/xiaopohai','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      小破孩系列之经典是毋庸置疑的，它具有强烈的民族特色和很高观赏性，主角“小破孩”和“小丫”两个胖乎乎的卡通形象，造型简洁流畅，具有典型的中国风格得到人们的强烈支持与喜爱。小破孩系列包括故事、儿歌和游戏，各种体验等着你。','/d/file/p/xiaopohai.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('30','春天的故事','0','70','20','s/chuntiangushi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      春天的故事想必大家都知道，一位老人在中国南海边画了一个圈，然后神奇般的崛起一座座城市，高楼大厦，歌唱春天为了纪念那位老人，同样唱出了春天心声。春天的故事里演绎着不平凡的人生，谱写着不一样的故事，我们一起来记录它。','/d/file/p/chuntiandegushi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('31','奥特曼','0','70','20','s/aoteman','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      相信没有小朋友不知道奥特曼的吧，他以人类守护者的角色存在着，保护着人类的安全，防止一切外星人的侵入与破坏，一次次打到那邪恶的怪兽。这种强大的正面形象让每个小朋友都记住了他，人人想当奥特曼，在奥特曼游戏中，你就是那个强大的化身，去守护你的家园吧。','/d/file/p/aoteman.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('32','超级玛丽','0','70','20','s/chaojimali','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      小朋友，著名的《超级玛丽里》游戏是不是也玩过呢，一个靠着吃蘑菇成长，闻名世界的超级巨星，那个大鼻子、头戴帽子、身穿背带工作服、还留着胡子的可爱人物，你一定喜欢的不得了吧。在这里，你可以玩许许多多的不同种类的闯关游戏，让你玩转不停。','/d/file/p/chaojimali.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('33','迷宫小游戏','0','70','20','s/migong','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      每个人都存在一种冒险精神，小朋友也不例外哦，对于新鲜事物的尝试都是一种冒险的行为。迷宫小游戏，为小朋友提供了一个良好的平台，满足小朋友们的好奇心、斗胜心，通过闯过一重重管卡，让你得到满足于收获，同时更考究你的智力和判断能力。是一个非常不错的游戏，值得小朋友们勇敢尝试。','/d/file/p/migongxiaoyouxi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('34','木偶奇遇记','0','70','20','s/muou','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      当仁慈木匠皮帕诺睡觉的时候，梦见一位蓝色的天使赋予他最心爱的木偶皮诺曹生命，于是小木偶开始了他的冒险。如果他要成为真正的男孩，他必须通过勇气、忠心以及诚实的考验。是不是很好听呢，《木偶奇遇记》小朋友们最爱听的睡前故事，妈妈们要记得每天晚上给自己的宝宝看一集哦。让自己的宝宝在睡梦中也去完成那勇气、忠心和城市的考验，教导孩子做个善良的人。','/d/file/p/muouqiyuji.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('35','宝宝学汉字','0','70','20','s/bbxuehanzi','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      中国民族5000年悠久的历史，存在着太多太多的国粹艺术。中国的汉子博大精深，每个中国人都必须去学习我们祖先流传创造出来的文字，它不仅代表一种历史，更是一种文化。妈妈们是宝宝的第一个启蒙老师，要好好的教宝宝们学习汉子哦。','/d/file/p/xuehanzi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('36','三字经','0','70','20','s/sanzijing','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      《三字经》作为三大国学启蒙读物之一，是中华民族珍贵的文化遗产，它短小精悍、琅琅上口，千百年来，家喻户晓。其内容涵盖了历史、天文、地里、道德以及一些民间传说，所谓“熟读《三字经》，可知千古事”。对于这种国粹，作为华夏名族的我们，怎可不读呢，被历代中国人奉为经典并不断流传。作为祖国的未来种子，妈妈们一定要教宝宝读《三字经》哦，让孩子们把民族文化流传下去。','/d/file/p/sanzijing.jpg','1','1','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('37','名人故事','0','70','20','s/mingren','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      中华五千年历史中，有着太多太多的名人、英雄的存在。有的是在沙场上抗战保卫家园的英雄；有的是文人学士，站在大殿中指点江山。在他们身上有着太多可歌可泣的感人故事，他们每一个人的经历都可以编写成一部小说，他们经历过无数的传奇。祖国大地正因为有这样的英雄人物存在而强盛不衰，他们一次次的在危难中挺身而出保卫着自己的同胞骨肉。作为后人，我们必须得诵读他们的故事，以他们为榜样，永远记得那些可爱的人。','/d/file/p/mingrengushi.jpg','1','0','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('38','经典儿歌','0','70','20','s/jingdian','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      每天都有各种各样的新歌出炉，但能让人记住并流传下来的那些，才能够称之为经典。经典儿歌，都是小朋友喜欢听的儿歌，各种各样的你想听的都能在这里找到，《让我们荡起双桨》都是我们耳熟能详的。','/d/file/p/jingdianerge.jpg','1','1','0','','1','0','0','0','0');");
E_D("replace into `phome_enewszt` values('39','海洋知识百科','0','70','20','s/haiyangbaike','.html','10','','10','10','0','10','10','1','0','4','game','newstime DESC','      海洋，对于人来说是陌生的，也是向往的。海洋的面积远远大于陆地，对于海洋的认识，人们认识的知识皮毛而已，但就这么一点就让人类受益匪浅，这就为它披上了一层神秘的面纱。波澜壮阔的海洋里是一个神奇的世界，哪里有各种各样的鱼类，你都喊不来名字，在这里你能知道什么样的鱼长什么样的，让你对于大海有一个全新的了解，让你知道海洋是神奇与伟大。','/d/file/p/haiyangzhishi.jpg','1','0','0','','1','0','0','0','0');");
