E_D("DROP TABLE IF EXISTS `phome_gamepk_gameinfo`;");
E_C("CREATE TABLE `phome_gamepk_gameinfo` (
  `gid` smallint(6) unsigned NOT NULL AUTO_INCREMENT,
  `sid` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gname` varchar(50) NOT NULL,
  `greadme` varchar(255) DEFAULT NULL,
  `gtype` varchar(255) NOT NULL DEFAULT 'PK',
  `ggroup` varchar(255) DEFAULT NULL,
  `gswf` varchar(255) NOT NULL,
  `gmaxicon` varchar(255) NOT NULL,
  `gminicon` varchar(255) DEFAULT NULL,
  `gstor` tinyint(1) NOT NULL DEFAULT '0',
  `gbestscore` varchar(10) DEFAULT NULL,
  `gbestuid` mediumint(8) unsigned DEFAULT NULL,
  `gbestusername` varchar(15) DEFAULT NULL,
  `gfee` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gpkfee` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gpfextcredits` varchar(13) NOT NULL DEFAULT 'extcredits1',
  `gprextcredits` varchar(13) NOT NULL DEFAULT 'extcredits1',
  `gprize1` smallint(6) unsigned NOT NULL DEFAULT '50',
  `gprize2` smallint(6) unsigned NOT NULL DEFAULT '20',
  `gprize3` smallint(6) unsigned NOT NULL DEFAULT '10',
  `gprize4` smallint(6) unsigned NOT NULL DEFAULT '5',
  `gprizen` smallint(6) unsigned NOT NULL DEFAULT '2',
  `gprizex` smallint(6) unsigned NOT NULL DEFAULT '1',
  `ghot` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gtimeper` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gnumper` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gnextid` smallint(6) unsigned NOT NULL DEFAULT '1',
  `gexttag` smallint(6) unsigned NOT NULL DEFAULT '0',
  `gtypeid` smallint(6) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`gid`)
) ENGINE=MyISAM AUTO_INCREMENT=136 DEFAULT CHARSET=utf8");
